import React, { useState } from 'react';
import axios from 'axios'; // You may need to install axios

function GithubUserSearch() {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`https://api.github.com/users/${username}`);
      setUserData(response.data);
      setError(null);
    } catch (err) {
      setUserData(null);
      setError('User not found!');
    }
  };

  return (
    <div>
      <h1 className='text-black text-2xl font-sans font-bold p-2'>Github User Search</h1>
      {/* <input
        type="text"
        placeholder="Enter GitHub username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      /> */}
      {/*
  Heads up! ðŸ‘‹

  Plugins:
    - @tailwindcss/forms
*/}
{/*
  Heads up! ðŸ‘‹

  Plugins:
    - @tailwindcss/forms
*/}

<div className="relative">
  <label htmlFor="Search" className="sr-only"> Search </label>

  <input
    type="text"
    id="Search"
    placeholder="Search for..."
    className="w-full rounded-md border-gray-200 py-2.5 pe-10 shadow-sm sm:text-sm"
  />

  <span className="absolute inset-y-0 end-0 grid w-10 place-content-center">
    <button type="button" className="text-gray-600 hover:text-gray-700">
      <span className="sr-only">Search</span>

      <svg
        xmlns="http://www.w3.org/2000/svg"
        fill="none"
        viewBox="0 0 24 24"
        strokeWidth="1.5"
        stroke="currentColor"
        className="h-4 w-4"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          d="M21 21l-5.197-5.197m0 0A7.5 7.5 0 105.196 5.196a7.5 7.5 0 0010.607 10.607z"
        />
      </svg>
    </button>
  </span>
</div>


      {/* <button onClick={handleSearch}>Search</button> */}

      {/* Base */}

<a
  className="group relative inline-block text-sm font-medium text-black focus:outline-none focus:ring active:text-black"
  onClick={handleSearch}
>
  <span
    className="absolute inset-0 translate-x-0.5 translate-y-0.5 bg-black transition-transform group-hover:translate-y-0 group-hover:translate-x-0"
  ></span>

  <span className="relative block border border-current bg-white px-8 py-3">
    Search
  </span>
</a>

{/* Hover */}

<a
  className="group relative inline-block text-sm font-medium text-black focus:outline-none focus:ring active:text-black"
  onClick={handleSearch}
>
  <span
    className="absolute inset-0 translate-x-0 translate-y-0 bg-black transition-transform group-hover:translate-y-0.5 group-hover:translate-x-0.5"
  ></span>


</a>
      {error && <p>{error}</p>}
      {userData && (
        <div>
          <h2>{userData.login}</h2>
          <img src={userData.avatar_url} alt="User Avatar" />
          <p>Followers: {userData.followers}</p>
          <p>Following: {userData.following}</p>
          <p>Public Repos: {userData.public_repos}</p>
        </div>
      )}
    </div>
  );
}

export default GithubUserSearch;
